import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../css/App.css';
import { DataGrid } from '@material-ui/data-grid';

const columns2 = [
  { field: 'id', headerName: 'ID', width: 70 },
  { field: 'firstName', headerName: 'First name', width: 130 },
  { field: 'lastName', headerName: 'Last name', width: 130 },
  {
    field: 'age',
    headerName: 'Age',
    type: 'number',
    width: 90,
  },
  {
    field: 'fullName',
    headerName: 'Full name',
    description: 'This column has a value getter and is not sortable.',
    sortable: false,
    width: 160,
    valueGetter: (params) =>
      `${params.getValue('firstName') || ''} ${params.getValue('lastName') || ''}`,
  },
];
const rows2 = [
  { id: 1, lastName: 'Snow', firstName: 'Jon', age: 35 },
  { id: 2, lastName: 'Lannister', firstName: 'Cersei', age: 42 },
  { id: 3, lastName: 'Lannister', firstName: 'Jaime', age: 45 },
  { id: 4, lastName: 'Stark', firstName: 'Arya', age: 16 },
  { id: 5, lastName: 'Targaryen', firstName: 'Daenerys', age: null },
  { id: 6, lastName: 'Melisandre', firstName: null, age: 150 },
  { id: 7, lastName: 'Clifford', firstName: 'Ferrara', age: 44 },
  { id: 8, lastName: 'Frances', firstName: 'Rossini', age: 36 },
  { id: 9, lastName: 'Roxie', firstName: 'Harvey', age: 65 },
];

function Home() {

  // viene eseguito appena viene caricata la pagina
  useEffect(() => {
    fetchData();
  }, []);

  const [posts, setPosts] = useState([]);

  // chiamata fake per richiedere i dati 
  const fetchData = async () => {
    const rawData = await fetch('https://jsonplaceholder.typicode.com/posts');

    // adesso trasformo i dati ricevuti in json
    const data = await rawData.json();

    const posts = data.slice(0, 10);    // con slice prendiamo solo i primi 10 elementi dell'array
    console.log(posts);

    setPosts(posts);
  }

  const columns = [
    { field: 'userId', headerName: 'Nome', width: 130 },
    { field: 'id', headerName: 'Data', width: 130 },
    { field: 'title', headerName: 'Correttezza', width: 130 },
    { field: 'body', headerName: 'Dettagli', width: 130 },
  ];

  return (
    <div className="App">
      <h1>HOME</h1>

      <h2>Carosello</h2>
      <h2>SITI | SOCIAL</h2>

      <div style={{ height: 400, width: '100%' }}>
        <DataGrid rows={rows} columns={columns} pageSize={5} checkboxSelection />
      </div>

      {/* posts.map(post => (
        <Link key={post.id} to={`/${post.id}`}>
          <h4 key={post.id} >{post.title}</h4>
        </Link>
      )) */}
    </div>
  );
}

export default Home;
